discoverUrl:
@js:
(() => {
    let url = '';
    if (params.tabIndex == 0) {
        url = host + '/vodshow/'+params.filters.cat+'-' +
            params.filters.area + '-' +
            params.filters.sort + '-' +
            params.filters.type + '-----' +
            params.pageIndex + '---' +
            params.filters.year + '.html';
    } else if (params.tabIndex == 1) {
        url = host + '/vodshow/1-' +
            params.filters.area + '-' +
            params.filters.sort + '-' +
            params.filters.type + '-' +
            params.filters.lang + '----' +
            params.pageIndex + '---' +
            params.filters.year + '.html';
    } else if (params.tabIndex == 2) {
        url = host + '/vodshow/4-' +
            params.filters.area + '-' +
            params.filters.sort + '-' +
            params.filters.type + '-' +
            params.filters.lang + '----' +
            params.pageIndex + '---' +
            params.filters.year + '.html';
    } else if (params.tabIndex == 3) {
        url = host + '/vodshow/3-' +
            params.filters.area + '-' +
            params.filters.sort + '-' +
            params.filters.type + '-' +
            params.filters.lang + '----' +
            params.pageIndex + '---' +
            params.filters.year + '.html';
    } else if (params.tabIndex == 4) {
        url = host + '/label/netflix/page/' + params.pageIndex + '.html';
    } else if (params.tabIndex == 5) {
        url = host + '/vodshow/124--' + params.filters.sort + '------' + params.pageIndex + '---' + '.html';
    }
    return {
        'url': url,
    };
})()

chapterRoads:
@js:
(async () => {
    let praseResult = [];
    let Roads = await xpath(result, "//div[contains(@class,\"module-tab-item tab-item\")]/@data-dropdown-value");
    let ChapterList = await xpath(result, "//div[@class=\"module-play-list\"]/node()");
    print(ChapterList.length);

    for (let index = 0; index < ChapterList.length; index++) {
        let item_road = [];
        let list = (await xpath(ChapterList[index], "//div[contains(@class,\"module-play-list-content\")]/node()"));
        for (let j = 0; j < list.length; j++) {
            let title = (await xpath(list[j], "//a/text()")).join('');
            let url = (await xpath(list[j], "//a/@href")).join('');
            item_road.push({
                'title': title,
                'url': url,
            });
        }
        praseResult.push({
            'roadName': Roads[index],
            'items': item_road,
        });
    }
    //print(JSON.stringify(praseResult));
    return praseResult;
})()
